package com.janbask.assigmentOperators;

public class AssignmentOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a=100;
		System.out.println(a); // 100
		a+=50;  // 100 + 50
		System.out.println(a);
		a-=30;  // 150 - 30
		System.out.println(a);
		a*=2; //120 *2
		System.out.println(a);

	}

}
